import { useState, useEffect } from "react";
import TextField from "../Input/TextField";
import FileInput from "../Input/FileInput";
import Button from "../Button";
import {IconButton} from "@mui/material";
import CloseIcon from '@mui/icons-material/Close';
import styles from "./styles.module.scss";
import defaultCloset from "../../images/defaultCloset.png";

const ClosetsModel = ({closeModel, closet}) => {
    const [data, setData] = useState({
        desc: "",
        img: "",
    });

    useEffect(() => {
        if (closet) {
            setData(prev => ({
                ...prev,
                desc: closet?.desc || "",
                img: closet?.img || defaultCloset,
            }));
        }
    }, [closet]);

    const handleInputState = (name, value) => {
        setData((prev) => ({...prev, [name]: value}));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        alert(data);
    };

    return(
        <div className={styles.model_container}>
            <IconButton className={styles.close_btn} onClick={closeModel}>
                <CloseIcon />
            </IconButton>
            <div className={styles.input_container}>
                <TextField
                    label="Desc"
                    name="desc"
                    value={data.desc}
                    handleInputState={handleInputState}
                />
            </div>
            <div className={styles.input_container}>
                <FileInput
                    label="Choose Image"
                    name="img"
                    type="file"
                    value={data.img}
                    handleInputState={handleInputState}
                />
            </div>
            <Button
                label="Submit"
                onClick={handleSubmit}
                style={{position: "absolute", bottom:"0", right:"0", margin: "1rem"}}
            />
        </div>
    );
};

export default ClosetsModel;
