import { Fragment } from "react";
import Closets from "../../Components/Closets";
import styles from "./styles.module.scss";
import Button from "../../Components/Button";
import { Link } from "react-router-dom";
import { useState, useEffect } from 'react';

const closets = [
    {_id: 1, img: "", desc: "Default Closet 1"},
    {_id: 2, img: "", desc: "Default Closet 2"},
];

const Home = () => {
    const [userId, setUserId] = useState('');
    const [token, setToken] = useState('');
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/api/users/${userId}`, {
                    headers: {
                        'x-auth-token': token,
                    }
                });
                setUserId(response.data.userId); 
                setToken(response.data.token); 
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };

        fetchData();
    }, [token]);

    return(
        <Fragment>
            <NavBar userId={userId} token={token} />
            <div className={styles.container}>
                <div className={styles.top}>
                    <h1>Your Closets</h1>
                    <Link to="/create_closet">
                        <Button label="Create New Closet" style={{ width: "20rem", marginRight: "5rem"}} />
                    </Link>

                </div>

                <div className={styles.closets_container}>
                    <Closets closets={closets} />
                </div>
            </div>
        </Fragment>
    );
};

export default Home;
