import { Fragment, useState, useEffect } from "react";
import Closets from "../../Components/Closets";
import styles from "./styles.module.scss";
import Button from "../../Components/Button";
import { Link } from "react-router-dom";
import NavBar from "../../Components/Navbar";
import setAuthToken from '../../setAuthToken';
import axios from "axios";


const closets = [
    {_id: 1, img: "", desc: "Default Closet 1"},
    {_id: 2, img: "", desc: "Default Closet 2"},
];


const Home = () => {
    const [userData, setUserData] = useState(null);
    const [closetData, setClosetData] = useState(null);
    useEffect(() => {
        async function fetchData() {
            const token = localStorage.getItem('token');
            const response = await axios.get(`http://localhost:8080/api/closet/`, {
                headers: {
                    'x-auth-token': token,
                },
            });
            const closets = [response.data];
            setClosetData(closets);
        }
        const storedUserData = localStorage.getItem('userData');

        if (storedUserData) {
            try {
                const parsedUserData = JSON.parse(storedUserData);
                setUserData(parsedUserData.data);
                console.log(userData);
                fetchData();
                console.log(closetData);
                // if (token) {
                //   setAuthToken(token);
                // }
                // get closet data by api

            } catch (error) {
                console.error('Error parsing userData:', error);
            }
        }
    }, []);

    return(
        <Fragment>
            <div className={styles.container}>
                <div className={styles.top}>
                    <h1>Your Closets</h1>
                    <Link to="/create_closet">
                        <Button label="Create New Closet" style={{ width: "20rem", marginRight: "5rem"}} />
                    </Link>

                </div>

                <div className={styles.closets_container}>
                    {/*userData.closet == [] ? (<h2>No Closet</h2>) : (<></>)*/}

                </div>
            </div>
        </Fragment>
    );
};

export default Home;
