import { Fragment } from "react";
import Closets from "../../Components/Closets";
import styles from "./styles.module.scss";
import Button from "../../Components/Button";
import { Link, useLocation } from "react-router-dom";
import NavBar from "../../Components/Navbar";

const closets = [
    {_id: 1, img: "", desc: "Default Closet 1"},
    {_id: 2, img: "", desc: "Default Closet 2"},
];

const Home = () => {
    const [userData, setUserData] = useState('');

    useEffect(() => {
        const storedUserData = localStorage.getItem('userData');
        if (storedUserData) {
            setUserData(storedUserData);
        }
    }, []);

    return(
        <Fragment>
            <NavBar userData={userData} />
            <div className={styles.container}>
                <div className={styles.top}>
                    <h1>Your Closets</h1>
                    <Link to="/create_closet">
                        <Button label="Create New Closet" style={{ width: "20rem", marginRight: "5rem"}} />
                    </Link>

                </div>

                <div className={styles.closets_container}>
                    <Closets closets={closets} />
                </div>
            </div>
        </Fragment>
    );
};

export default Home;
