import { Link } from "react-router-dom";
import { Fragment } from "react";
import styles from "./styles.module.scss";
import { ClickAwayListener } from "@mui/material";
import {MoreHoriz} from "@mui/icons-material";
import Dropdown from 'react-bootstrap/Dropdown';


const Closets = ({closets, closeMenu}) => {
    return(
        <Fragment>
            {closets.map(c => (

                    <div className={styles.closet}>
                        <Dropdown>
                            <Dropdown.Toggle variant="success" id="dropdown-basic">
                                Dropdown Button
                            </Dropdown.Toggle>

                            <Dropdown.Menu>
                                <Dropdown.Item href="/">Action</Dropdown.Item>
                                <Dropdown.Item href="/">Another action</Dropdown.Item>

                            </Dropdown.Menu>
                        </Dropdown>
                        <Link key={c._id} to={`/closet/${c._id}`}>
                            {c.img === "" 
                            ? (<img src="https://images.unsplash.com/photo-1614631446501-abcf76949eca?q=80&w=1287&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt={c.name} />)
                            : (<img src={c.img} alt={c.name}/>
                            )}
                            <p>{c.desc}</p>
                        </Link>
                    </div>
            ))
            }
        </Fragment>
    );
};

export default Closets;
