import { useState, Fragment, useEffect } from "react";
import Closets from "../../Components/Closets";
import ClosetModel from "../../Components/ClosetModel";
import Clothes from "../../Components/Clothes";
import Button from "../../Components/Button";
import styles from "./styles.module.scss";
import { useParams, useNavigate } from 'react-router-dom';
import { Link } from "react-router-dom";
import * as React from 'react';
import {Tabs, Tab, Box, Menu, MenuItem} from '@mui/material';
import { styled } from '@mui/system';

const CustomMenuItem = styled(MenuItem)({
    fontSize: '1.5rem', // Customize font size here
});


const clothes1 = [
    {
        name: "My Bag",
        img: "https://media.bergdorfgoodman.com/f_auto,q_auto:low,ar_5:7,c_fill,dpr_2.0,w_720/01/bg_4035878_100273_m",
        category: "Bag",
        color: "Brown",
        brand: "prada",
        season: "Summer",
        _id: 1
    },
    {
        name: "Just black-t",
        img: "https://images.unsplash.com/photo-1583743814966-8936f5b7be1a?q=80&w=1287&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        category: "Top",
        color: "Black",
        brand: "No-brand",
        season: "Summer",
        _id: 2
    },
]

const clothes2 = [
    {
        name: "Prada Bag",
        img: "https://media.bergdorfgoodman.com/f_auto,q_auto:low,ar_5:7,c_fill,dpr_2.0,w_720/01/bg_4035878_100273_m",
        category: "Bag",
        color: "Brown",
        brand: "prada",
        season: "Summer",
        _id: 1
    },
    {
        name: "Black Tommy T-shirt",
        img: "https://images.unsplash.com/photo-1583743814966-8936f5b7be1a?q=80&w=1287&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        category: "Top",
        color: "Black",
        brand: "Tommy",
        season: "Fall",
        _id: 2
    },
]

const closets = [
    {_id: 1, img: "", desc: "Default Closet 1", clothes: clothes1},
    {_id: 2, img: "", desc: "Default Closet 2", clothes: clothes2},
];

const ClosetsPage = () => {
    const [model, setModel] = useState(false);
    const { id } = useParams();
    const closetId = parseInt(id);
    const navigate = useNavigate();
    const [anchorEl, setAnchorEl] = useState(null);
    const [tabValue, setTabValue] = React.useState('all');
    const [menuType, setMenuType] = useState('');
    const [categoryValue, setCategoryValue] = useState('');
    const [colorValue, setColorValue] = useState('');
    const [seasonValue, setSeasonValue] = useState('');
    const [loading, setLoading] = useState(true);

    const categoryMenuItems = [
        { label: 'Top', value: 'Top' },
        { label: 'Down', value: 'Down' },
        { label: 'Shoes', value: 'Shoes' },
        { label: 'Outerwear', value: 'Outerwear' },
        { label: 'Bag', value: 'Bag' },
        { label: 'Accessory', value: 'Accessory' },
        { label: 'Jewellery', value: 'Jewellery' },
        { label: 'Other', value: 'Other' },
    ];

    const colorMenuItems = [
        { label: 'White', value: 'White' },
        { label: 'Black', value: 'Black' },
        { label: 'Gray', value: 'Gray' },
        { label: 'Brown', value: 'Brown' },
        { label: 'Beige', value: 'Beige' },
        { label: 'Camel', value: 'Camel' },
        { label: 'Red', value: 'Red' },
        { label: 'Orange', value: 'Orange' },
        { label: 'Yellow', value: 'Yellow' },
        { label: 'Green', value: 'Green' },
        { label: 'Blue', value: 'Blue' },
        { label: 'Navy', value: 'Navy' },
        { label: 'Purple', value: 'Purple' },
        { label: 'Pink', value: 'Pink' },
        { label: 'Silver', value: 'Silver' },
        { label: 'Gold', value: 'Gold' },
        { label: 'Misc', value: 'Misc' },
        { label: 'Other', value: 'Other' },
    ];

    const seasonMenuItems = [
        { label: 'Spring', value: 'Spring' },
        { label: 'Summer', value: 'Summer' },
        { label: 'Fall', value: 'Fall' },
        { label: 'Winter', value: 'Winter' },
        { label: 'Other', value: 'Other' },
    ];

    const handleChange = (event, newValue) => {
        setTabValue(newValue);
        if (newValue !== 'all') {
            setAnchorEl(event.currentTarget);
            setMenuType(newValue);
        } else {
            setAnchorEl(null);
        }
    };

    const handleCategoryClick = (event) => {
        setAnchorEl(event.currentTarget);
        setMenuType('category');
    };

    const handleColorClick = (event) => {
        setAnchorEl(event.currentTarget);
        setMenuType('color');
    };

    const handleSeasonClick = (event) => {
        setAnchorEl(event.currentTarget);
        setMenuType('season');
    };

    const handleClose = (value) => {
        setAnchorEl(null);
        setMenuType('');
        setCategoryValue(value);
        console.log(value);
    };


    const [selectedCloset, setSelectedCloset] = useState({});

    useEffect(() => {
      const storedClosetData = localStorage.getItem('closetData');
      if (storedClosetData) {
        try {
          const parsedClosetData = JSON.parse(storedClosetData);
          setSelectedCloset([parsedClosetData.data].find(c => c._id === closetId));

        } catch (error) {
          console.error('Error parsing Data:', error);
        }
      }
      setLoading(false);
    }, []);


    useEffect(() => {
        if (!selectedCloset && !loading) {
            navigate("/not-found");
        }
    }, [selectedCloset, navigate, loading]);

    return(
        <Fragment>
            {loading ? (
                <p>Loading...</p> // Show loading indicator while loading
            ) : (
                selectedCloset ? (
                    <div className={styles.container}>
                        <div className={styles.top}>
                            <h1>{selectedCloset.desc}</h1>
                            <Link to="/create_clothes">
                                <Button label="Create New Clothes" style={{ width: "20rem", marginRight: "5rem"}} />
                            </Link>
                        </div>
                        <Box sx={{ width: '100%' }} style={{marginLeft: "2rem"}}>
                            <Tabs
                                value={tabValue}
                                onChange={handleChange}
                                aria-label="secondary tabs example"
                            >
                                <Tab value="all" label="All" style={{fontSize: "1.6rem"}}/>
                                <Tab value="category" label="By Category" style={{fontSize: "1.6rem"}} onClick={handleCategoryClick}/>
                                <Tab value="color" label="By Color" style={{fontSize: "1.6rem"}} onClick={handleColorClick}/>
                                <Tab value="season" label="By Season" style={{fontSize: "1.6rem"}} onClick={handleSeasonClick}/>
                            </Tabs>
                            <Menu
                                anchorEl={anchorEl}
                                keepMounted
                                open={Boolean(anchorEl)}
                                onClose={() => handleClose('')}
                            >
                                {menuType === 'category' &&  categoryMenuItems.map(item => (
                                    <CustomMenuItem
                                        key={item.value}
                                        onClick={() => handleClose(item.value)}
                                        className={styles.customMenuItem}
                                    >
                                        {item.label}
                                    </CustomMenuItem>
                                ))}
                                {menuType === 'color' &&  colorMenuItems.map(item => (
                                    <CustomMenuItem
                                        key={item.value}
                                        onClick={() => handleClose(item.value)}
                                        className={styles.customMenuItem}
                                    >
                                        {item.label}
                                    </CustomMenuItem>
                                ))}
                                {menuType === 'season' &&  seasonMenuItems.map(item => (
                                    <CustomMenuItem
                                        key={item.value}
                                        onClick={() => handleClose(item.value)}
                                        className={styles.customMenuItem}
                                    >
                                        {item.label}
                                    </CustomMenuItem>
                                ))}
                            </Menu>
                        </Box>

                        <div className={styles.closets_container}>
                            {selectedCloset.clothes.map(cloth => (
                                <Fragment key={cloth._id}>
                                    <Clothes clothes={cloth} />
                                </Fragment>
                            ))}
                        </div>
                    </div>
                ) : (
                    <p>Closet not found</p> // Show error message if closet not found
                )
            )}
        </Fragment>
    );
};

export default ClosetsPage;
