import { useState, Fragment, useEffect } from "react";
import Closets from "../../Components/Closets";
import ClosetModel from "../../Components/ClosetModel";
import { IconButton } from "@mui/material";
import Clothes from "../../Components/Clothes";
import { AccessTime, Edit, Delete } from "@mui/icons-material";
import styles from "./styles.module.scss";
import { useParams, useNavigate } from 'react-router-dom';
import { Link } from "react-router-dom";
import * as React from 'react';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';


const clothes1 = [
    {
        img: "https://media.bergdorfgoodman.com/f_auto,q_auto:low,ar_5:7,c_fill,dpr_2.0,w_720/01/bg_4035878_100273_m",
        category: "Bag",
        color: "Brown",
        brand: "prada",
        season: "Summer",
        _id: 1
    },
    {
        img: "https://images.unsplash.com/photo-1583743814966-8936f5b7be1a?q=80&w=1287&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        category: "Top",
        color: "Black",
        brand: "No-brand",
        season: "Summer",
        _id: 2
    },
]

const clothes2 = [
    {
        img: "https://media.bergdorfgoodman.com/f_auto,q_auto:low,ar_5:7,c_fill,dpr_2.0,w_720/01/bg_4035878_100273_m",
        category: "Bag",
        color: "Brown",
        brand: "prada",
        season: "Summer",
        _id: 1
    },
    {
        img: "https://images.unsplash.com/photo-1583743814966-8936f5b7be1a?q=80&w=1287&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        category: "Top",
        color: "Black",
        brand: "Tommy",
        season: "Fall",
        _id: 2
    },
]

const closets = [
    {_id: 1, img: "", desc: "Default Closet 1", clothes: clothes1},
    {_id: 2, img: "", desc: "Default Closet 2", clothes: clothes2},
];

const ClosetsPage = () => {
    const [model, setModel] = useState(false);
    const { id } = useParams();
    const closetId = parseInt(id);
    const navigate = useNavigate();

    const selectedCloset = closets.find(c => c._id === closetId);

    useEffect(() => {
        if (!selectedCloset) { navigate("/not-found")}
    }, [selectedCloset, navigate]);

    const [value, setValue] = React.useState('all');

    const handleChange = (event, newValue) => {
      setValue(newValue);
    };

    return(
        selectedCloset 
        ? (
            <div className={styles.container}>
                <h1>{selectedCloset.desc}</h1>
                <Box sx={{ width: '100%' }} style={{marginLeft: "2rem"}}>
                    <Tabs
                        value={value}
                        onChange={handleChange}
                        aria-label="secondary tabs example"
                    >
                        <Tab value="all" label="All" style={{fontSize: "1.5rem"}}/>
                        <Tab value="category" label="By Category" />
                        <Tab value="color" label="By Color" />
                        <Tab value="brand" label="By Brand" />
                        <Tab value="season" label="By Season" />
                    </Tabs>
                </Box>
                <div className={styles.closets_container}>
                    {selectedCloset.clothes.map(cloth => (
                        <Fragment key={cloth._id}>
                        <Clothes clothes={cloth} />
                        </Fragment>
                    ))}
                </div>
            </div>
        )
        : (
            <Link to="/not-found" />
        )
    );
};

export default ClosetsPage;
