import { useState, useEffect } from "react";
import TextField from "../Input/TextField";
import FileInput from "../Input/FileInput";
import Button from "../Button";
import Select from "../Input/Select";
import styles from "./styles.module.scss";
import defaultImage from "../../images/defaultClothes.png";

const categoryOptions = [
    'Top', 
    'Down',
    'Shoes', 
    'Outerwear', 
    'Bag', 
    'Accessory', 
    'Jewellery', 
];

const colorMenuItems = [
    { label: 'White', value: 'White' },
    { label: 'Black', value: 'Black' },
    { label: 'Gray', value: 'Gray' },
    { label: 'Brown', value: 'Brown' },
    { label: 'Beige', value: 'Beige' },
    { label: 'Camel', value: 'Camel' },
    { label: 'Red', value: 'Red' },
    { label: 'Orange', value: 'Orange' },
    { label: 'Yellow', value: 'Yellow' },
    { label: 'Green', value: 'Green' },
    { label: 'Blue', value: 'Blue' },
    { label: 'Navy', value: 'Navy' },
    { label: 'Purple', value: 'Purple' },
    { label: 'Pink', value: 'Pink' },
    { label: 'Silver', value: 'Silver' },
    { label: 'Gold', value: 'Gold' },
    { label: 'Misc', value: 'Misc' },
];

const seasonMenuItems = [
    { label: 'Spring', value: 'Spring' },
    { label: 'Summer', value: 'Summer' },
    { label: 'Fall', value: 'Fall' },
    { label: 'Winter', value: 'Winter' },
];

const ClothesModel = ({clothes}) => {
    const [data, setData] = useState({
        name: "",
        category: "",
        img: "",
        color: "",
        brand: "",
        season: ""
    });



    useEffect(() => {
        setData(prev => ({
            ...prev,
            img: clothes?.img || defaultImage,
        }));
    }, [clothes]);

    const handleInputState = (name, value) => {
        setData((prev) => ({ ...prev, [name]: value }));

        if (name === "img") {
            const file = value;
            const reader = new FileReader();


            if (file) {
                reader.readAsDataURL(file);
            }
        }
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("desc", data.desc);
        formData.append("img", data.img);
        alert("Description: " + data.desc + ", Image: " + (data.img ? data.img.name : "No file selected"));
        console.log(JSON.stringify(data));
    };

    return(

        <div className={styles.model_container}>
            <form onSubmit={handleSubmit} className={styles.form_container}>
                <div className={styles.input_container}>
                    <TextField
                        label="Name"
                        name="name"
                        value={data.name}
                        handleInputState={handleInputState}
                        InputProps={{ classes: { input: styles.topAlignedText } }}
                    />
                </div>
                <h1>Upload Clothes' Image</h1>
                <div className={styles.input_container}>
                    <input
                        type="file"
                        id="file-input"
                        name="img"
                        onChange={(e) => handleInputState("img", e.target.files[0])}
                    />
                </div>
                <div className={styles.input_container}>
                    <Select 
                        name="category"
                        handleInputState={handleInputState}
                        label="Category"
                        placeholder="Category"
                        options={categoryOptions}
                        value={data.category}
                        required={true}
                        style={{color: "var(--gray)", fontSize: "2.2rem"}}
                    />
                </div>
                <div className={styles.input_container}>
                    <Button
                        label="Create"
                        onClick={handleSubmit}
                    />
                </div>
            </form>
        </div>
    );
};

export default ClothesModel;
