import { Link } from "react-router-dom";
import { Fragment } from "react";
import styles from "./styles.module.scss";
import { ClickAwayListener } from "@mui/material";
import {ArrowRight} from "@mui/icons-material";

const Closets = ({closets, closeMenu}) => {
    return(
        <Fragment>
            {closets.map(c => (
                <Link key={c._id} to={`/closet/${c._id}`}>
                    <div className={styles.closet}>
                        <ClickAwayListener onClickAway={closeMenu}>
                            <div className={styles.menu} onClick={closeMenu}>
                                <div className={styles.closet_option}>
                                    <p>Edit</p>
                                </div>
                                <div className={styles.closet_option}>
                                    <p>Delete</p>
                                </div>
                            </div>
                        </ClickAwayListener>
                        {c.img === "" 
                        ? (<img src="https://images.unsplash.com/photo-1614631446501-abcf76949eca?q=80&w=1287&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt={c.name} />)
                        : (<img src={c.img} alt={c.name}/>
                        )}
                        <p>{c.desc}</p>
                    </div>


                </Link>
            ))
            }
        </Fragment>
    );
};

export default Closets;
