import { Fragment, useState, useEffect } from "react";
import Closets from "../../Components/Closets";
import styles from "./styles.module.scss";
import Button from "../../Components/Button";
import { Link } from "react-router-dom";
import NavBar from "../../Components/Navbar";
import setAuthToken from '../../setAuthToken';
import axios from "axios";


const closets = [
    {_id: 1, img: "", desc: "Default Closet 1"},
    {_id: 2, img: "", desc: "Default Closet 2"},
];


const Home = () => {
    const [closetData, setClosetData] = useState([]);

    const [userData, setUserData] = useState(null);

    const fetchData = async() => {
        try {
            const token = localStorage.getItem('token');
            if (token) {
                setAuthToken(token);
            }
            const response = await axios.get(`http://localhost:8080/api/closet/`, {
                headers: {
                    'x-auth-token': token,
                },
            });
            setClosetData(response.data);
        }
        catch (error) {
            console.error('Error fetching closet data:', error);
        }
    }

    useEffect(() => {
        const storedUserData = localStorage.getItem('userData');
        if (storedUserData) {
            try {
                const parsedUserData = JSON.parse(storedUserData);
                setUserData(parsedUserData.data);
            } catch (error) {
                console.error('Error parsing userData:', error);
            }
        }
        fetchData();
    }, []);

    console.log(userData);
    console.log(closetData.data);

    return(
        <Fragment>
            <div className={styles.container}>
                <div className={styles.top}>
                    <h1>Your Closets</h1>
                    <Link to="/create_closet">
                        <Button label="Create New Closet" style={{ width: "20rem", marginRight: "5rem"}} />
                    </Link>

                </div>

                <div className={styles.closets_container}>

                <Closets closets={[closetData.data]} />
                </div>
            </div>
        </Fragment>
    );
};

export default Home;
