import { useState, useEffect } from "react";
import TextField from "../Input/TextField";
import FileInput from "../Input/FileInput";
import Button from "../Button";
import {IconButton} from "@mui/material";
import CloseIcon from '@mui/icons-material/Close';
import styles from "./styles.module.scss";
import defaultImage from "../../images/defaultCloset.png";

const ClosetsModel = ({closeModel, closet}) => {
    const [data, setData] = useState({
        desc: "",
        img: "",
    });


    const [imagePreview, setImagePreview] = useState(defaultImage);

    useEffect(() => {
        if (closet) {
            setData(prev => ({
                ...prev,
                desc: closet?.desc || "",
                img: closet?.img || defaultImage,
            }));
            setImagePreview(closet?.img || defaultImage);
        }
    }, [closet]);

    const handleInputState = (name, value) => {
        setData((prev) => ({ ...prev, [name]: value }));

        if (name === "img") {
            const file = value;
            const reader = new FileReader();

            reader.onloadend = () => {
                setImagePreview(reader.result);
            };

            if (file) {
                reader.readAsDataURL(file);
            } else {
                setImagePreview(defaultImage);
            }
        }
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        alert(data);
    };

    return(

        <div className={styles.model_container}>
            <IconButton className={styles.close_btn} onClick={closeModel}>
                <CloseIcon />
            </IconButton>

            <form onSubmit={handleSubmit} className={styles.form_container}>
                <div className={styles.input_container}>
                    <TextField
                        label="Description"
                        name="desc"
                        value={data.desc}
                        handleInputState={handleInputState}
                    />
                </div>
                <h1>Upload Closet's Image</h1>
                <div className={styles.input_container}>
                    {/*<FileInput
                        label="Choose Image"
                        name="img"
                        type="file"
                        value={data.img}
                        handleInputState={handleInputState}
    />*/}
                <input
                    type="file"
                    id="file-input"
                    name="img"
                    onChange={(e) => handleInputState("img", e.target.files[0])}
                />
                <img src={imagePreview} alt="Preview" style={{ width: "100px", height: "100px" }} />
                </div>
                <div className={styles.input_container}>
                    <Button
                        label="Submit"
                        onClick={handleSubmit}
                    />
                </div>
            </form>
        </div>
    );
};

export default ClosetsModel;
